    def number_press(self, number):
        expression = self.lineEdit.text()
        self.lineEdit.setText(expression + number)
    def operator_press(self, operator):
        expression = self.lineEdit.text()
        self.lineEdit.setText(expression + operator)
    def back_press(self):
        expression = self.lineEdit.text()

        if len(expression)>0:
            expression = expression[:-1]
            self.lineEdit.setText(str(expression))

    def clear_press(self):
            self.lineEdit.clear()

    def Equal_press(self):
        try:
            expression = self.lineEdit.text()
            result = eval(expression)
            self.lineEdit.setText(str(result))


        except:
            self.lineEdit.setText('error')






